/* 
域名被墙检测

青龙环境变量 domains  多域名使用@分割，填写完整网址http(s)://www.google.com

使用第三方接口，每日限额10次，适量运行，氪金可加成

域名异常发送通知消息，正常只打印输出日志

 */

const jsname = '域名被墙检测'
const $ = Env(jsname)
const logDebug = 0
const notifyFlag = 1; //0为关闭通知，1为打开通知,默认为1
const notify = $.isNode() ? require('./sendNotify') : '';
let notifyStr = ''
let httpResult //global buffer
let userList = []
let domains = ($.isNode() ? process.env.domains : $.getdata('domains')) || '';
let userIdx = 0
let versionLocal = '1.0'
let Changelog = ''
!(async () => {
        if(!(await checkEnv())) return
        await tips();
        console.log('\n================== 开始检测 ==================')
        for(let user of userList) {
            index = ++userIdx
            await checkDomain(user);
         }
        await showmsg()
        
    
 })()
.catch((e) => $.logErr(e))
.finally(() => $.done())
async function checkDomain(user){
    let url = `http://check.uomg.com/api/urlsec/wall?token=&domain=${user}`
    let host = (url.split('//')[1]).split('/')[0]
    let urlObject = {
        url:url,
        headers: {
            'Host' : host,
            'Connection' : 'keep-alive',
            'Accept-Language' : 'zh-CN,zh-Hans;q=0.8',
            'Accept-Encoding' : 'gzip',
            'User-Agent': 'Mozilla/5.0 (Windows NT 6.1; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/102.0.0.0 Safari/537.36',
        },
    }
    await httpRequest('get',urlObject)
    let result = httpResult;
    if(!result) return
    //console.log(result)
    if(result.code == 200){
        console.log(`域名[${index}][${user}]检测成功：${result.msg},剩余检测次数：${result.num}`)
    }else if(result.code == 201 ){
        console.log(`域名[${index}][${user}]检测成功：${result.msg},剩余检测次数：${result.num}`)
        notifyStr += `\n域名[${index}][${user}]检测成功：${result.msg},剩余检测次数：${result.num}`

    }else{
        console.log(`域名[${index}][${user}]检测失败：${result.msg}`)
        notifyStr += `\n域名[${index}][${user}]检测失败：${result.msg}`
    }
}
async function versionCheck(name,type){
    if(type == 1){
        versionUrl = `https://raw.githubusercontent.com/todayqaq/Scripts/main/${name}.js`
    }else if(type == 2){
        versionUrl = ``
    }
    let url = versionUrl
    let host = (url.split('//')[1]).split('/')[0]
    let urlObject = {
        url:url,
        headers: {
            'Host' : host,
            'Connection' : 'keep-alive',
            'Accept-Language' : 'zh-CN,zh-Hans;q=0.8',
            'Accept-Encoding' : 'gzip',
            'User-Agent': 'Mozilla/5.0 (Linux; Android 10; MI 8 Lite Build/QKQ1.190910.002;wv)AppleWebKit/537.36 (KHTML, like Gecko) Version/4.0 Chrome/96.0.4664.92 Mobile Safari/537.36jiuzhang/android',
        },
    }
    await httpRequest('get',urlObject)
    let result = httpResult;
    if(!result) return
    //console.log(result)
    return result.match(/VersionCheck = "([\d\.]+)"/)[1]
}
async function tips() {
	let Version_latest = await versionCheck('checkGFW_QAQ', '1');
	let Version = `📌 本地脚本: ${versionLocal}  远程仓库脚本:  ${Version_latest}`
	console.log(`${Version}\n📌 🆙 更新日志: ${Changelog}`);	
}
async function showmsg() {
    if(!notifyStr) return
    notifyBody = jsname + "运行通知\n\n" + notifyStr
    if (notifyFlag == 1) {
        $.msg(notifyBody);
        if($.isNode()){await notify.sendNotify($.name, notifyBody );}
    } else {
        console.log(notifyBody);
    }
}
async function checkEnv() {
    if(domains) {
        for(let domain of domains.split('@')) {
            if(domain) userList.push(domain)
        }
        userCount = userList.length
    } else {
        console.log('未找到域名')
        return;
    }
    
    console.log(`共找到${userCount}个域名`)
    return true
}
async function httpRequest(method,url) {
    httpResult = null
    if(method == 'post') {
        url.headers['Content-Type'] =  'application/x-www-form-urlencoded'
        if(url.body) {
            url.headers['Content-Length'] = url.body.length
        } else {
            url.headers['Content-Length'] = 0
        }
    }
    return new Promise((resolve) => {
        $[method](url, async (err, resp, data) => {
            try {
                if (err) {
                    console.log(`${method}请求失败`);
                    console.log(JSON.stringify(err));
                    $.logErr(err);
                } else {
                    str_resp_header=JSON.stringify(resp.headers).replace(/-/g,'_').search('"content_type":"text/plain; charset=utf_8",')
                    //console.log(str_resp_header)
                    if(str_resp_header > 0){
                        httpResult = data;
                        if(logDebug) console.log(httpResult);
                    }else if (safeGet(data)) {
                        httpResult = JSON.parse(data);
                        if(logDebug) console.log(httpResult);
                    }
                }
            } catch (e) {
                $.logErr(e, resp);
            } finally {
                resolve();
            }
        });
    });
}
function safeGet(data) {
    try {
        if (typeof JSON.parse(data) == "object") {
            return true;
        } else {
            console.log(data)
        }
    } catch (e) {
        console.log(e);
        console.log(`服务器访问数据为空，请检查自身设备网络情况`);
        return false;
    }
}
